import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [playlistUrl, setPlaylistUrl] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [result, setResult] = useState(null);
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [user, setUser] = useState(null);

  useEffect(() => {
    document.body.className = isDarkMode ? 'dark-theme' : 'light-theme';

    // Check for callback parameters
    const params = new URLSearchParams(window.location.search);
    if (params.has('accessToken')) {
      const userData = {
        accessToken: params.get('accessToken'),
        refreshToken: params.get('refreshToken'),
        userId: params.get('userId'),
        displayName: params.get('displayName'),
      };
      setUser(userData);
      // Clean up URL
      window.history.replaceState({}, document.title, window.location.pathname);
    }
  }, [isDarkMode]);

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost:4000/login');
      const data = await response.json();
      window.location.href = data.url;
    } catch (error) {
      setError('Failed to initiate Spotify login');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!user) {
      setError('Please login with Spotify first');
      return;
    }

    setIsLoading(true);
    setError(null);
    setResult(null);

    try {
      if (!playlistUrl.trim()) {
        throw new Error('Please enter a playlist URL');
      }

      const response = await fetch('http://localhost:4000/convert', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          url: playlistUrl,
          accessToken: user.accessToken
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to convert playlist');
      }

      setResult(data);
    } catch (error) {
      console.error('Error:', error);
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <div className={`App ${isDarkMode ? 'dark-theme' : 'light-theme'}`}>
      <header className="App-header">
        <div className="logo-container">
          <div className="brand">
            <span className="brand-text">Playlist</span>
            <span className="brand-text-len">Converter</span>
          </div>
          <span className="version">v3.0</span>
        </div>
        <div className="theme-toggle">
          <button onClick={toggleTheme} className="icon-button">
            {isDarkMode ? 'üåô' : '‚òÄÔ∏è'}
          </button>
          {!user ? (
            <button onClick={handleLogin} className="login-button">
              Login with Spotify
            </button>
          ) : (
            <span className="user-info">
              Welcome, {user.displayName}
            </span>
          )}
          <a href="https://github.com/abhiiee-sharma" 
             className="icon-button"
             target="_blank" 
             rel="noopener noreferrer">
            <img 
              src={isDarkMode ? "assets/github.svg" : "/github-nigh"} 
              alt="GitHub"
            />
          </a>
        </div>
      </header>

      <main className="main-content">
        <h2>Convert any playlist between YouTube and Spotify</h2>
        <p className="description">
          Enter a playlist URL from either platform to convert it to the other.
          {!user && ' Please login with Spotify first.'}
        </p>

        <form onSubmit={handleSubmit} className="convert-form">
          <textarea
            value={playlistUrl}
            onChange={(e) => setPlaylistUrl(e.target.value)}
            placeholder="Enter playlist URL (YouTube or Spotify)"
            className={`url-input ${error ? 'error' : ''}`}
            disabled={isLoading || !user}
          />

          <button 
            type="submit" 
            className="analyze-button" 
            disabled={isLoading || !user}
          >
            {isLoading ? 'Converting...' : 'Convert Playlist'}
          </button>
        </form>

        {error && (
          <div className="error-container">
            <p className="error-message">{error}</p>
          </div>
        )}

        {isLoading && (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <p>Converting your playlist...</p>
          </div>
        )}

        {result && (
          <div className="result-container">
            <h3>Conversion Complete!</h3>
            <p>Found {result.songs.length} songs</p>
            <a href={result.url} 
               className="result-link" 
               target="_blank" 
               rel="noopener noreferrer">
              Open in {result.platform === 'spotify' ? 'Spotify' : 'YouTube'}
            </a>

            <div className="songs-list">
              {result.songs.map((song, index) => (
                <div key={index} className="song-item">
                  <span className="song-title">{song.title}</span>
                  <span className="song-artist">{song.artist}</span>
                </div>
              ))}
            </div>
          </div>
        )}
      </main>

      <footer className="footer">
        <p>Made with ‚ù§Ô∏è by Abhishek Sharma</p>
      </footer>
    </div>
  );
}

export default App;
